insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (1, 1, 1, '미역은 물에 담가 10분 정도 불려주세요.', '/image1/ListIcon/recipePic/1recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (2, 1, 2, '불린 미역은 먹기 좋게 썬 후 물기를 제거해 주세요.', '/image1/ListIcon/recipePic/1recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (3, 1, 3, '냄비에 참기름을 두르고 미역을 볶아주세요.', '/image1/ListIcon/recipePic/1recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (4, 1, 4, '미역이 부드러워지면 국간장을 넣고 2분정도 더 볶아주세요.', '/image1/ListIcon/recipePic/1recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (5, 1, 5, '다시마 육수를 넣고 ⅔ 정도 넣고 10분 정도 끓여주세요.', '/image1/ListIcon/recipePic/1recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (6, 1, 6, '나머지 다시마 육수를 모두 넣고 성게알과 액젓을 넣어 5분 정도 더 끓여주세요. 부족한 간은 소금으로 맞춰주세요.', '/image1/ListIcon/recipePic/1recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (7, 1, 7, '완성된 성게미역국을 그릇에 담고 밥과 함께 즐겨주세요.', '/image1/ListIcon/recipePic/1recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (8, 2, 1, '곤드레는 깨끗이 씻어 4cm 길이로 잘라주세요.', '/image1/ListIcon/recipePic/2recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (9, 2, 2, '볼에 반죽 재료와 곤드레를 넣고 섞어주세요.', '/image1/ListIcon/recipePic/2recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (10, 2, 3, '볼에 양념장 재료를 넣어 섞어주세요.', '/image1/ListIcon/recipePic/2recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (11, 2, 4, '달군 팬에 약간의 기름을 두른 후 반죽을 올려 얇게 펼쳐주세요. 앞뒤로 노릇하게 구워주세요.', '/image1/ListIcon/recipePic/2recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (12, 2, 5, '곤드레 전에 양념장을 곁들여 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/2recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (13, 3, 1, '불린 미역은 물에 30초 정도 데친 후 체에 밭쳐 깨끗이 씻어주세요.', '/image1/ListIcon/recipePic/3recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (14, 3, 2, '물기를 짠 후 먹기 좋은 크기로 잘라주세요.', '/image1/ListIcon/recipePic/3recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (15, 3, 3, '볼에 미역을 담은 후 양념 재료를 넣어 골고루 섞어주세요.', '/image1/ListIcon/recipePic/3recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (16, 3, 4, '오이는 얇게 슬라이스로 잘라주세요.', '/image1/ListIcon/recipePic/3recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (17, 3, 5, '미역이 들어있는 볼에 오이와 물을 넣어 섞어주세요.', '/image1/ListIcon/recipePic/3recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (18, 3, 6, '마지막에 얼음, 통깨를 뿌려 완성해주세요.', '/image1/ListIcon/recipePic/3recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (19, 4, 1, '아욱의 줄기 끝 부분은 꺾어내고 껍질을 벗겨주세요.', '/image1/ListIcon/recipePic/4recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (20, 4, 2, '큰 잎은 먹기 좋은 크기로 찢어주세요.', '/image1/ListIcon/recipePic/4recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (21, 4, 3, '손질한 아욱은 흐르는 물에 2~3번 씻어주세요. 줄기가 두꺼운 경우 반으로 썰어주세요.', '/image1/ListIcon/recipePic/4recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (22, 4, 4, '청양고추와 대파는 어슷 썰어주세요.', '/image1/ListIcon/recipePic/4recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (23, 4, 5, '냄비에 멸치다시마육수를 붓고 된장과 고추장을 넣어 센불에서 끓여 주세요. 육수가 끓어 오르면 아욱과 다진마늘, 청양고추를 넣고 끓여주세요. 마지막으로 대파를 넣어 한소끔 끓인 후 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/4recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (24, 5, 1, '김치는 꼭지를 붙인 채 3등분하여 썰어주세요.', '/image1/ListIcon/recipePic/5recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (25, 5, 2, '볼에 양념 재료를 넣고 섞어 양념장을 만들어주세요.', '/image1/ListIcon/recipePic/5recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (26, 5, 3, '목살은 큼직하게 잘라주세요.', '/image1/ListIcon/recipePic/5recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (27, 5, 4, '양파는 굵게 채썰어주세요. 대파와 고추는 어슷하게 썰어주세요.', '/image1/ListIcon/recipePic/5recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (28, 5, 5, '냄비에 김치를 담고 목살과 양파를 올린 후 양념장과 멸치다시마육수, 된장을 넣어주세요.', '/image1/ListIcon/recipePic/5recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (29, 5, 6, '끓기 시작하면 불을 중간불로 줄여 1시간 20분 정도 끓여주세요.', '/image1/ListIcon/recipePic/5recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (30, 5, 7, '마지막에 대파와 홍고추, 청양고추를 넣고 한소끔 끓인 후 목살을 먹기 좋은 크기로 잘라 김치와 함께 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/5recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (31, 6, 1, '오이를 가늘게 채 썰어주세요.', '/image1/ListIcon/recipePic/6recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (32, 6, 2, '김치는 굵게 다져주세요.', '/image1/ListIcon/recipePic/6recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (33, 6, 3, '볼에 다진 김치와 양념재료를 넣고 섞어주세요.', '/image1/ListIcon/recipePic/6recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (34, 6, 4, '끓는 물에 중면을 펼쳐 넣고 면끼리 달라붓지 않도록 저어주세요. 물이 끓어오르면 찬물을 2~3회 반복해서 부어 국수를 삶아주세요. 흐르는 찬물에 중면을 여러 번 씻어 전분기를 없애고 체반에 밭쳐 물기를 빼주세요.', '/image1/ListIcon/recipePic/6recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (35, 6, 5, '양념장에 삶은 국수를 넣고 버무려주세요.', '/image1/ListIcon/recipePic/6recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (36, 6, 6, '접시에 비빔국수를 담고 삶은 달걀과 오이채, 깨를 올려주세요.', '/image1/ListIcon/recipePic/6recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (37, 7, 1, '해감한 꼬막은 깨끗이 비벼 씻어주세요. 끓는 물에 청주를 넣어 온도를 떨어뜨린 다음 꼬막을 넣고 한쪽으로 저어주면서 삶다가 입이 벌어지기 시작하면 체로 건져주세요.', '/image1/ListIcon/recipePic/7recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (38, 7, 2, '삶은 꼬막은 살만 발라주세요.', '/image1/ListIcon/recipePic/7recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (39, 7, 3, '볼에 양념 재료를 넣어 섞어주세요.', '/image1/ListIcon/recipePic/7recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (40, 7, 4, '대파와 청고추는 송송 썰고, 홍고추는 반으로 갈라 씨를 뺀 후 얇게 썰어주세요. 양파는 잘게 다져주세요.', '/image1/ListIcon/recipePic/7recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (41, 7, 5, '볼에 양념과 썰어둔 야채, 꼬막살을 넣어 버무려주세요.', '/image1/ListIcon/recipePic/7recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (42, 7, 6, '접시에 담아 통깨를 뿌려 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/7recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (43, 8, 1, '소고기는 물에 30분 정도 담가 핏물을 제거해주세요.', '/image1/ListIcon/recipePic/8recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (44, 8, 2, '양파, 당근은 채 썰고 대파는 길이 7~8cm, 두께 0.5cm 크기로 채 썰어주세요.', '/image1/ListIcon/recipePic/8recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (45, 8, 3, '배는 껍질을 벗기고 믹서에 곱게 간 뒤 소고기에 넣어주세요.', '/image1/ListIcon/recipePic/8recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (46, 8, 4, '진간장, 국간장을 넣고 골고루 섞어주세요.', '/image1/ListIcon/recipePic/8recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (47, 8, 5, '순서 4에 올리고당, 후추, 설탕, 마늘, 맛술을 넣고 잘 버무린 뒤 냉장고에서 30분 정도 숙성시키고 꺼내 기름을 두르지 않은 팬에 넣고 센불로 5분 정도 볶아주세요.', '/image1/ListIcon/recipePic/8recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (48, 8, 6, '양파, 당근을 넣고 당근이 익을 때까지 2분 정도 볶아주세요.', '/image1/ListIcon/recipePic/8recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (49, 8, 7, '볶은 불고기 위에 채 썬 대파를 올려서 맛있게 즐겨보세요!', '/image1/ListIcon/recipePic/8recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (50, 9, 1, '감자는 껍질을 벗기고 채칼을 이용해 3mm 정도로 채 썰어주세요.', '/image1/ListIcon/recipePic/9recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (51, 9, 2, '감자가 부서지지 않도록 찬물에 살살 헹궈 전분질을 빼고 물에 5분 정도 담가주세요.', '/image1/ListIcon/recipePic/9recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (52, 9, 3, '체에 밭쳐 물기를 빼주세요.', '/image1/ListIcon/recipePic/9recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (53, 9, 4, '당근은 감자와 같은 두께로 채썰고 양파는 얇게 썰어주세요.', '/image1/ListIcon/recipePic/9recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (54, 9, 5, '달군 팬에 기름을 넉넉히 두르고 다진 마늘을 넣고 볶아주세요. 양파, 당근, 감자를 넣어 중간불에서 볶아주세요.', '/image1/ListIcon/recipePic/9recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (55, 9, 6, '감자가 투명해지면 소금을 넣어 간을 맞춰주세요.', '/image1/ListIcon/recipePic/9recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (56, 9, 7, '마지막에 통깨를 뿌려 완성해 주세요.', '/image1/ListIcon/recipePic/9recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (57, 10, 1, '불린 미역은 물에 깨끗하게 헹군 후 체에 밭쳐 물기를 빼주세요.', '/image1/ListIcon/recipePic/10recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (58, 10, 2, '달군 팬에 쇠고기를 넣어 볶다가 참기름을 넣고 쇠고기 겉면의 핏기가 없을 때까지 약불에서 볶아주세요.', '/image1/ListIcon/recipePic/10recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (59, 10, 3, '다진 마늘을 넣어 볶다가 국간장과 미역을 넣고 3~4분 정도 볶아주세요.', '/image1/ListIcon/recipePic/10recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (60, 10, 4, '물을 넣고 20~30분 정도 충분히 끓여주세요.', '/image1/ListIcon/recipePic/10recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (61, 10, 5, '부족한 간은 국간장과 액젓으로 맞춰주세요.', '/image1/ListIcon/recipePic/10recipe5.jpg');
