insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (76, 12, 3, '냄비에 다시마물 5컵을 넣은 후 끓어오르면 떡국용 떡을 넣고, 떡이 익어 부드러워지면 국물양념 재료와 대파를 넣어 1분 정도 더 끓여주세요.', '/image1/ListIcon/recipePic/12recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (77, 12, 4, '그릇에 떡국을 담고 쇠고기, 대파, (지단 또는 달걀말이) 등을 기호에 따라 올려주세요.', '/image1/ListIcon/recipePic/12recipe4.jpg');
