insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (79, 13, 2, '무는 4센치 길이로 굵게 채썰고, 고추는 어슷하게 썰어주세요. 콩나물은 다듬어 깨끗이 씻고 실파는 4센치 길이로 썰어주세요.', '/image1/ListIcon/recipePic/13recipe2.jpg');
