insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (85, 14, 1, '오징어는 껍질과 내장을 제거하고, 다리와 함께 먹기 좋은 크기로 잘라주세요.', '/image1/ListIcon/recipePic/14recipe1.jpg');
