insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (88, 14, 4, '무가 투명해지면 오징어와 양념 재료를 넣어 약 5분간 끓여주세요.', '/image1/ListIcon/recipePic/14recipe4.jpg');
