insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (97, 16, 1, '묵은지와 두부는 적당한 크기로 썰어주세요. 양파는 채썰고, 대파와 청양고추는 어슷 썰어주세요.', '/image1/ListIcon/recipePic/16recipe1.jpg');
