insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (115, 18, 7, '그릇에 밥을 담고 밥 위에 볶은 양파와 볶은 스팸, 끝 쪽에는 스크램블을 둘러 담아주세요.', '/image1/ListIcon/recipePic/18recipe7.jpg');
