insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (129, 20, 6, '끓는 물에 쫄면을 넣고 저어가며 3~4분간 삶아주세요.', '/image1/ListIcon/recipePic/20recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (130, 20, 7, '찬물에 쫄면을 여러 번 씻어 전분기를 없애고 채반에 받쳐 물기를 빼주세요.', '/image1/ListIcon/recipePic/20recipe7.jpg');
