insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (271, 41, 1, '두부는 3×4cm 크기로 납작하게 썰어 물기를 제거하고 마른 팬에 노릇하게 구워주세요. 실곤약은 끓는 물에 살짝 데친 후 찬물에 담갔다가 물기를 제거해 준비합니다.', '/image1/ListIcon/recipePic/41recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (272, 41, 2, '표고버섯은 기둥을 자르고 가운데 별모양으로 칼집을 넣어주세요. 배추 잎은 세로로 3등분하고 양파는 4등분합니다. 대파는 어슷하게 썰고 쑥갓은 큼직하게 썰어주세요.', '/image1/ListIcon/recipePic/41recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (273, 41, 3, '냄비에 국물 재료를 넣고 설탕이 녹을 때까지 2분만 끓입니다. 전골냄비에 쇠고기, 두부, 실곤약, 표고버섯, 배추, 양파, 대파를 돌려 담고 국물을 부어 끓여주세요.', '/image1/ListIcon/recipePic/41recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (274, 41, 4, '국물이 졸아들면 끓이는 중간에 다시마 육수를 넣어 간을 조절하고 쑥갓을 넣습니다.', '/image1/ListIcon/recipePic/41recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (275, 41, 5, '그릇에 달걀을 풀고 익힌 재료를 건져서 찍어 먹습니다.', '/image1/ListIcon/recipePic/41recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (276, 41, 6, '기호에 따라 남은 국물에 우동면이나 볶음밥 재료를 넣어 익혀 먹어도 좋아요.', '/image1/ListIcon/recipePic/41recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (277, 42, 1, '미소시루 베이스 재료를 모두 섞어 된장 베이스를 만들어 주세요. 이때 다시마는 2cm x 2cm로 잘라 넣어 주세요.', '/image1/ListIcon/recipePic/42recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (278, 42, 2, '가쓰오 다시 5컵에 미소시루 베이스 1큰술, 정종, 미림을 넣고 끓으면 바로 끄고 체로 걸러주세요.', '/image1/ListIcon/recipePic/42recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (279, 42, 3, '건미역은 불려 적당히 자르고, 팽이버섯은 3등분 해주세요. 두부는 1cm x 1cm로 잘라 주세요.', '/image1/ListIcon/recipePic/42recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (280, 42, 4, '그릇에 두부, 미역, 팽이버섯을 넣고 미소시루 국물을 부으면 완성이예요.', '/image1/ListIcon/recipePic/42recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (281, 43, 1, '옥도미는 비늘과 내장을 제거하고 머리를 자른 후 세 장 뜨기 해주세요. 머리와 뼈는 손질해서 물에 담가 피를 빼줍니다. 옥도미는 30g(1인분) 정도로 자르고 소금을 뿌려 놓아 주세요.', '/image1/ListIcon/recipePic/43recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (282, 43, 2, '머리와 뼈는 170도 오븐에서 15분간 구워주세요.', '/image1/ListIcon/recipePic/43recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (283, 43, 3, '물에 다시마, 머리와 뼈, 토막낸 무를 넣고, 1시간 동안 끓여 육수를 만들어 주세요. 무가 다 익으면 2cm x 2cm로 잘라 주세요.', '/image1/ListIcon/recipePic/43recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (284, 43, 4, '순서 1의 옥도미 살은 160도 오븐에서 5분간 구워 주고, 대파는 0.5cm로 어슷 썰어 프라이팬에 볶아 주세요.', '/image1/ListIcon/recipePic/43recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (285, 43, 5, '육수에 물, 옥도미, 대파를 넣고 끓인 후, 그릇에 담고 무, 유자 껍질을 첨가해 주시면 완성이예요.', '/image1/ListIcon/recipePic/43recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (286, 44, 1, '무와 어묵, 곤약은 먹기 좋은 크기로 잘라주세요.', '/image1/ListIcon/recipePic/44recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (287, 44, 2, '어묵은 뜨거운 물을 끼얹어 겉의 기름을 제거하고, 곤약은 끓는 물에 살짝 데쳐주세요. 데친 곤약은 가운데 칼집을 내어 꼬아 놓고, 어묵은 먹기 좋게 꼬치에 꽂아주세요.', '/image1/ListIcon/recipePic/44recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (288, 44, 3, '마늘은 편으로 썰고, 대파는 반으로 가른 후 4센티 길이로 썰어주세요.', '/image1/ListIcon/recipePic/44recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (289, 44, 4, '냄비에 다시마물 4컵과 무를 넣고 10~15분 정도 끓여주세요.', '/image1/ListIcon/recipePic/44recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (290, 44, 5, '끓인 국물에 국물 재료와 마늘편, 대파, 건고추를 넣고 3분 정도 끓이다가 어묵과 곤약, 삶은 달걀을 돌려 담고 5분간 더 끓여주세요.', '/image1/ListIcon/recipePic/44recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (291, 44, 6, '종지에 간장 1큰술과 약간의 연겨자를 넣어 어묵전골과 곁들여주세요.', '/image1/ListIcon/recipePic/44recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (292, 45, 1, '냄비에 육수재료를 모두 넣고 10분간 끓이다 다시마를 건져낸 후 10분가량 더 끓여서 육수를 만들어 주시고 간장을 조금 넣어 간을 맞춰 주세요. 배추, 깻잎, 고기, 배추 순으로 차곡차곡 쌓으신 후 전골 냄비 높이에 맞춰서 잘라주세요.', '/image1/ListIcon/recipePic/45recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (293, 45, 2, '전골 냄비에 청경채를 깔아주신 후 잘라 놓은 배추와 고기를 겹겹이 담아주세요.', '/image1/ListIcon/recipePic/45recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (294, 45, 3, '마지막으로 버섯를 넣고 육수를 냄비의 2/3정도가 올라오도록 넣은 후 배추와 고기가 익을 때까지 끓여주세요.', '/image1/ListIcon/recipePic/45recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (295, 45, 4, '폰즈소스에 고기와 채소를 찍어서 드세요. 취향에 따라 시판용 스윗트 칠리소스와 함께 드셔도 좋아요. 남은 국물에는 국수를 넣어 삶아 드시거나 죽을 만들어서 드시면 좋아요.', '/image1/ListIcon/recipePic/45recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (296, 46, 1, '양파는 채썰고, 대파는 어슷하게 썰어주세요. 쪽파는 송송 썰어주세요.', '/image1/ListIcon/recipePic/46recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (297, 46, 2, '볼에 달걀을 담아 풀어주세요.', '/image1/ListIcon/recipePic/46recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (298, 46, 3, '160도의 기름에 돈가스를 넣고, 노릇하게 튀긴 후 적당한 크기로 썰어주세요.', '/image1/ListIcon/recipePic/46recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (299, 46, 4, '뚝배기 또는 냄비에 기름을 살짝 두르고, 다진마늘과 양파를 3분 정도 볶아주세요.', '/image1/ListIcon/recipePic/46recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (300, 46, 5, '양념 재료를 넣어 섞어주세요.', '/image1/ListIcon/recipePic/46recipe5.jpg');
