insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (303, 47, 1, '우엉은 깨끗이 씻어 곱게 채 썬 뛰 찬물에 담가 갈변을 막아주세요.', '/image1/ListIcon/recipePic/47recipe1.jpg');
