insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (305, 47, 3, '양파는 채 썰고, 대파는 얇게 어슷썰기 해주세요.', '/image1/ListIcon/recipePic/47recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (306, 47, 4, '따뜻한 물에 쇠고기 스톡을 넣어 녹인 뒤 나머지 나베 국물 재료를 모두 넣어 고루 섞어주세요.', '/image1/ListIcon/recipePic/47recipe4.jpg');
