insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (309, 47, 7, '넓은 냄비에 나베 국물을 붓고 우엉채를 넣어 뚜껑을 덮고 3~4분간 끓여주세요.', '/image1/ListIcon/recipePic/47recipe7.jpg');
