insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (312, 47, 10, '데친 쇠고기와 대파를 얹은 뒤 달걀을 냄비 가장자리를 따라 끼얹어주세요. 달걀이 익으면 그릇에 담아 마무리하세요.', '/image1/ListIcon/recipePic/47recipe10.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (313, 48, 1, '냄비에 물 1리터를 붓고 3분 30초간 가열해 주세요.', '/image1/ListIcon/recipePic/48recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (314, 48, 2, '물이 끓으면 면을 넣고 1분간 젓가락으로 저어가며 데친 후 채반에서 물기를 빼주세요.', '/image1/ListIcon/recipePic/48recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (315, 48, 3, '양파와 양배추는 도톰하게 썰어주세요. 당근은 얇게 편썰고 베이컨은 1cm 두께로 썰어 주세요.', '/image1/ListIcon/recipePic/48recipe3.jpg');
