insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (317, 48, 5, '양배추와 양파, 당근을 넣고 2분간 볶아주세요.', '/image1/ListIcon/recipePic/48recipe5.jpg');
