insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (319, 48, 7, '숙주와 새우를 넣고 1분 30초간 볶아주세요.', '/image1/ListIcon/recipePic/48recipe7.jpg');
