insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (321, 49, 1, '생수에 쯔유를 넣고 잘 저어준 후 냉동실에서 2~3시간 정도 얼려 시원하게 준비해 주세요.', '/image1/ListIcon/recipePic/49recipe1.jpg');
