insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (325, 49, 5, '끓는 물에 우동면을 넣고 1분 데친 후 얼음물에 담가 헹궈주세요.', '/image1/ListIcon/recipePic/49recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (326, 49, 6, '그릇에 면을 담고 쪽파와 레몬, 생강, 튀긴 고추를 올린 후 살얼음 낀 쯔유 국물을 자작하게 붓고 김을 올려 완성해 주세요.', '/image1/ListIcon/recipePic/49recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (327, 50, 1, '숙주는 끓는 물에 10초 정도 살짝 데쳐주세요.', '/image1/ListIcon/recipePic/50recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (328, 50, 2, '찬물에 헹군 후 체에 밭쳐 물기를 빼주세요.', '/image1/ListIcon/recipePic/50recipe2.jpg');
