insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (330, 50, 4, '달군 팬에 슬라이스 햄을 살짝 굽고 그릇에 덜어 준비해 주세요.', '/image1/ListIcon/recipePic/50recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (331, 50, 5, '팬에 식용유를 살짝 두르고 캔 옥수수를 넣어 볶아 물기를 제거해 주세요.', '/image1/ListIcon/recipePic/50recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (332, 50, 6, '냄비에 물과 된장, 라면수프를 넣은 후 끓으면 면을 넣고 3분 정도 끓여주세요.', '/image1/ListIcon/recipePic/50recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (333, 50, 7, '완성 그릇에 라면을 담아주세요.', '/image1/ListIcon/recipePic/50recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (334, 50, 8, '데친 숙주, 슬라이스 햄, 캔 옥수수, 잘게 썬 파, 달걀, 버터를 올려 완성해 주세요.', '/image1/ListIcon/recipePic/50recipe8.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (335, 51, 1, '대파는 6cm 길이로 잘라주세요.', '/image1/ListIcon/recipePic/51recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (336, 51, 2, '달군 팬에 닭다리살을 올려주세요. 소금, 후춧가루로 간을 하여 노릇하게 구운 후 먹기 좋은 크기로 잘라주세요.', '/image1/ListIcon/recipePic/51recipe2.jpg');
