insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (340, 51, 6, '냄비에 다시마 멸치 육수를 넣어 끓으면 볶은 대파와 닭고기를 넣어 끓여주세요.', '/image1/ListIcon/recipePic/51recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (341, 51, 7, '국물 양념 재료를 넣어 끓인 후 데친 우동면을 넣어 1분간 끓여주세요.', '/image1/ListIcon/recipePic/51recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (342, 51, 8, '완성된 우동을 그릇에 담아 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/51recipe8.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (343, 52, 1, '베이컨과 양배추는 먹기 좋은 크기로 자르고, 양파는 채썰고 마늘은 편으로 썰어주세요. 페페론치니는 잘게 다져주세요.', '/image1/ListIcon/recipePic/52recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (344, 52, 2, '끓는 물에 약간의 소금과 스파게티면을 넣어 6분 정도 삶은 후 체에 밭쳐 물기를 빼주세요. 물기를 뺀 면을 넓은 접시에 펼쳐 담고, 약간의 올리브유 넣어 버무려주세요.', '/image1/ListIcon/recipePic/52recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (345, 52, 3, '팬에 기름을 두르고 달걀을 한쪽 면만 익혀 반숙으로 달걀후라이를 만들어주세요.', '/image1/ListIcon/recipePic/52recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (346, 52, 4, '팬에 약간의 올리브유를 두르고 마늘편과 양파, 페페론치니를 넣고 1분 정도 볶아주세요.', '/image1/ListIcon/recipePic/52recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (347, 52, 5, '양파가 반쯤 익으면 양배추와 베이컨을 넣고 볶다가, 소스와 삶은 스파게티면을 넣어 골고루 섞이도록 볶아주세요.', '/image1/ListIcon/recipePic/52recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (348, 52, 6, '그릇에 야끼스파게티를 담고, 가츠오부스를 올린 후 반숙 달걀후라이를 얹어주세요.', '/image1/ListIcon/recipePic/52recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (349, 53, 1, '쇠고기는 키친타올에 올려 핏물을 제거한 후 먹기 좋은 크기로 썰고, 양파는 채를 썰고, 대파는 4센치 길이로 가늘게 채를 썰어주세요. 달군 냄비에 버터를 넣고 녹인 후 양파를 넣어 2분간 볶다가 쇠고기, 다진마늘을 넣고 약간의 소금과 후추를 뿌려 2분간 익혀주세요.', '/image1/ListIcon/recipePic/53recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (350, 53, 2, '볶은 쇠고기에 다시마물 5컵을 넣고 국물이 끓어오르면 불을 줄여 카레가루를 두세번에 나눠 넣으며 잘 풀어주고, 10분 정도 더 끓인 후 약간의 소금과 후추로 간을 해주세요. 냄비에 물이 끓으면 우동면을 넣고 2~3분 삶아 건져낸 후 찬물에 헹궈 물기를 제거해 주세요.', '/image1/ListIcon/recipePic/53recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (351, 53, 3, '그릇에 우동면을 담고 카레국물을 넣은 후 채를 썬 파를 올려주세요.', '/image1/ListIcon/recipePic/53recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (352, 54, 1, '가쓰오부시를 뺀 육수 재료를 냄비에 넣고 끓어오르면 다시마를 건져내고 불을 줄여서 3분간 더 끓여주세요.', '/image1/ListIcon/recipePic/54recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (353, 54, 2, '불을 끄고 가쓰오부시를 넣어 10분간 우려낸 후 체에 거릅니다.', '/image1/ListIcon/recipePic/54recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (354, 54, 3, '가쓰오부시 육수 4컵에 국물 양념 재료를 섞어 불에 올려주세요. 한 번 끓어오르면 불을 끄고 냉장고에 넣어 차갑게 준비합니다.', '/image1/ListIcon/recipePic/54recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (355, 54, 4, '실파는 송송 썰고 김은 잘게 채 썰거나 부숴주세요.', '/image1/ListIcon/recipePic/54recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (356, 54, 5, '무를 강판에 갈아 준비해주세요.', '/image1/ListIcon/recipePic/54recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (357, 54, 6, '새우의 껍질과 내장, 꼬리의 물총주머니를 제거한 후 안쪽에 칼집을 넣어 새우등을 펴주고 소금, 후추로 밑간을 합니다.', '/image1/ListIcon/recipePic/54recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (358, 54, 7, '볼에 튀김 반죽 재료를 잘 섞고 튀김가루는 따로 넓은 그릇에 담아주세요. 새우에 튀김가루를 먼저 골고루 묻힌 후 튀김 반죽을 묻혀 160도로 달군 식용유에 튀깁니다.', '/image1/ListIcon/recipePic/54recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (359, 54, 8, '끓는 물에 메밀면을 4분간 삶고 찬물에 여러번 헹군 후 물기를 빼고 그릇에 담아주세요.', '/image1/ListIcon/recipePic/54recipe8.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (360, 54, 9, '준비해둔 국물과 얼음을 넣은 후 새우튀김, 실파, 김, 무, 무순, 고추냉이를 올려주세요.', '/image1/ListIcon/recipePic/54recipe9.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (361, 55, 1, '사골육수 500ml와 물 500ml를 섞은 후 약한 불에 끓여서 ''뜨거운 육수''를 준비해주세요. 다른 냄비에서 끓는 물에 생면을 넣어 삶고 찬물에 헹군 후 체에 받쳐 물기를 빼주세요.', '/image1/ListIcon/recipePic/55recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (362, 55, 2, '양파와 통마늘은 슬라이스하고 알배추는 가늘게 썰고, 대파와 청양고추는 어슷 썰어주세요.', '/image1/ListIcon/recipePic/55recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (363, 55, 3, '바지락은 소금물에 해감하고 홍합은 수염을 제거해주세요. 원하는 양만큼 넣어주시고 집에 있는 해산물들을 넣으셔도 좋아요. 새우는 껍질과 내장을 제거하시고, 오징어는 안쪽에 칼집을 낸 후 한입 크기로 썰어주세요.', '/image1/ListIcon/recipePic/55recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (364, 55, 4, '달군 팬에 식용유를 두르고 마늘과 청양고추를 볶아서 매운 향을 내주세요.', '/image1/ListIcon/recipePic/55recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (365, 55, 5, '양파와 대파를 볶다가 손질한 새우, 오징어, 바지락, 홍합을 넣고 청주 2큰술, 소금, 후추 넣고 센불에서 볶아주세요.', '/image1/ListIcon/recipePic/55recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (366, 55, 6, '알배추와 청양고추를 넣고 살짝 볶아주다가 ''뜨거운 육수''를 붓고 살짝 끓이다 소스를 넣어 간을 해주세요.', '/image1/ListIcon/recipePic/55recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (367, 55, 7, '청경채를 넣고 불을 꺼주세요.', '/image1/ListIcon/recipePic/55recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (368, 55, 8, '그릇에 삶은 면과 숙주를 담고 국물을 부어 마무리해주세요.', '/image1/ListIcon/recipePic/55recipe8.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (369, 56, 1, '육수 양념 재료를 넣어 섞어주세요. 냉장고에 넣어 차갑게 준비해주세요.', '/image1/ListIcon/recipePic/56recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (370, 56, 2, '실파는 송송 썰고 김은 가늘게 잘라주세요.', '/image1/ListIcon/recipePic/56recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (371, 56, 3, '무를 강판에 갈아 준비해주세요.', '/image1/ListIcon/recipePic/56recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (372, 56, 4, '톳 국수는 체에 밭쳐 흐르는 물에 헹군 후 물기를 빼주세요.', '/image1/ListIcon/recipePic/56recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (373, 56, 5, '그릇에 톳 국수를 담은 후 준비해둔 국물과 실파, 김, 무, 무순, 고추냉이, 얼음을 올려 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/56recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (374, 57, 1, '진간장, 꿀, 전분가루를 볼에 넣고 잘 섞어주세요. 전분가루가 모두 녹으면 소고기 안심 표면에 골고루 발라 밑간을 해주세요.', '/image1/ListIcon/recipePic/57recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (375, 57, 2, '소바는 끓는 물에 삶아낸 후 얼음물에 담갔다가 체에 밭쳐 한쪽에 두세요.', '/image1/ListIcon/recipePic/57recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (376, 57, 3, '소스 재료를 볼에 넣고 모두 섞어 준비해주세요.', '/image1/ListIcon/recipePic/57recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (377, 57, 4, '아보카도는 껍질과 씨를 제거하고 얇게 슬라이스 해주세요.', '/image1/ListIcon/recipePic/57recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (378, 57, 5, '센 불로 달군 팬에 올리브유를 두르고 소고기 양면을 바삭하게 구워주세요.', '/image1/ListIcon/recipePic/57recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (379, 57, 6, '바삭하게 구운 소고기를 팬에서 꺼내 실온에서 약 10분간 레스팅한 후 먹기 좋은 크기로 잘라주세요.', '/image1/ListIcon/recipePic/57recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (380, 57, 7, '그릇에 소바와 아보카도, 스테이크를 담은 후 다진 쪽파와 볶은 통깨를 뿌리고 소스를 부어 마무리 해주세요.', '/image1/ListIcon/recipePic/57recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (381, 58, 1, '깨끗이 씻은 오이와 깻잎은 가늘게 채를 썰어주세요. 삶은 달걀은 반으로 잘라주세요.', '/image1/ListIcon/recipePic/58recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (382, 58, 2, '믹서에 참깨를 넣어 갈아주세요.', '/image1/ListIcon/recipePic/58recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (383, 58, 3, '볼에 간 참깨와 나머지 참깨 소스 재료를 넣고 섞어 소스를 만들어주세요.', '/image1/ListIcon/recipePic/58recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (384, 58, 4, '냄비에 물을 넣고 팔팔 끓으면 메밀면을 넣고 5~6분 정도 삶아주세요.', '/image1/ListIcon/recipePic/58recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (385, 58, 5, '채반에 메밀국수를 건진 후 찬물에 헹구고 물기를 빼주세요.', '/image1/ListIcon/recipePic/58recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (386, 58, 6, '채반에 삶은 메밀면을 담고 오이채와 깻잎채, 삶은 달걀을 올려주세요. 볼에 소스를 담아 곁들여 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/58recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (387, 59, 1, '냄비에 육수 A 재료를 넣고 강불에서 끓여주세요. 육수가 끓으면 다시마만 건져내고 약불로 줄여 20분 더 끓입니다.', '/image1/ListIcon/recipePic/59recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (388, 59, 2, '육수를 끓인 냄비에 육수 B 재료를 넣고 약불에서 3분 더 끓인 뒤 건더기를 모두 건지고 육수 일부를 얼려놔 주세요.', '/image1/ListIcon/recipePic/59recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (389, 59, 3, '그릇에 삶은 메밀면을 넣고 차갑게 만든 육수를 부은 뒤 고명을 올려주세요. 무와 배는 갈아서 쓰셔야 해요.', '/image1/ListIcon/recipePic/59recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (390, 60, 1, '아보카도는 씨앗에 칼날이 닿도록 칼집을 넣은 후 양쪽을 잡고 서로 반대 방향으로 비틀어 자른 후, 씨앗과 과육을 분리해주세요. 껍질을 벗겨 일정한 간격으로 슬라이스해 일렬로 돌돌 말아주세요. 연어는 사방 2cm 크기의 큐브 모양으로 잘라주세요.', '/image1/ListIcon/recipePic/60recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (391, 60, 2, '볼에 고추냉이 간장 소스 재료를 섞은 후 연어를 넣어 골고루 섞어주세요.', '/image1/ListIcon/recipePic/60recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (392, 60, 3, '그릇에 밥과 밥 밑간 재료를 넣어 밑간을 한 후 재워 둔 연어를 얹고 아보카도를 가운데에 올려주세요.', '/image1/ListIcon/recipePic/60recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (393, 60, 4, '가운데에 달걀 노른자를 올린 후 쪽파와 통깨, 후춧가루를 뿌려주세요.', '/image1/ListIcon/recipePic/60recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img)values (394, 60, 5, '김 가루를 뿌린 후 골고루 섞어 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/60recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (508, 81, 1, '팽이버섯, 느타리버섯은 잘 씻어 밑동을 자르고 표고버섯은 1cm 두께로 슬라이스 해주세요.', '/image1/ListIcon/recipePic/81recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (509, 81, 2, '감자는 껍질을 벗기고 1cm 두께로 슬라이스한 뒤 2등분 하고 청경채는 잘 씻어서 다듬어주세요.', '/image1/ListIcon/recipePic/81recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (510, 81, 3, '오징어는 1.5cm 두께로 썰고 새우는 머리와 꼬리를 제거하고 껍질을 벗겨주세요.', '/image1/ListIcon/recipePic/81recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (511, 81, 4, '냄비에 물, 사골국물을 넣고 납작당면을 넣어 끓여주세요.', '/image1/ListIcon/recipePic/81recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (512, 81, 5, '순서 4의 냄비에 마라소스를 넣고 더 끓여주세요.', '/image1/ListIcon/recipePic/81recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (513, 81, 6, '팽이버섯, 느타리버섯, 표고버섯, 감자, 청경채를 넣고 끓여주세요.', '/image1/ListIcon/recipePic/81recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (514, 81, 7, '오징어, 새우, 소고기를 넣고 끓여주세요.', '/image1/ListIcon/recipePic/81recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (515, 81, 8, '땅콩버터, 마늘을 넣고 더 끓여주세요.', '/image1/ListIcon/recipePic/81recipe8.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (516, 81, 9, '갖가지 재료와 얼얼한 매운맛을 음미하며 맛있게 즐기세요.', '/image1/ListIcon/recipePic/81recipe9.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (517, 82, 1, '당근과 샐러리, 닭가슴살은 채 썰어 준비해 주세요.', '/image1/ListIcon/recipePic/82recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (518, 82, 2, '팬에 마늘을 볶아 마늘 향을 내주세요.', '/image1/ListIcon/recipePic/82recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (519, 82, 3, '닭가슴살을 넣어 볶아주세요.', '/image1/ListIcon/recipePic/82recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (520, 82, 4, '순서 3에 순서 1을 넣고 분량의 소스를 넣어 볶아주세요.', '/image1/ListIcon/recipePic/82recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (521, 82, 5, '물전분을 넣어 농도를 맞춰주세요.', '/image1/ListIcon/recipePic/82recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (522, 82, 6, '준비해둔 춘권피 위에 볶은 속재료를 넣어 감싸주세요.', '/image1/ListIcon/recipePic/82recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (523, 82, 7, '춘권피 모서리 부분에 밀가루 풀을 발라주세요.', '/image1/ListIcon/recipePic/82recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (524, 82, 8, '모양대로 접어주세요.', '/image1/ListIcon/recipePic/82recipe8.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (525, 82, 9, '양 끝의 길이를 맞추어 가며 접어주세요.', '/image1/ListIcon/recipePic/82recipe9.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (526, 82, 10, '돌돌 말아줍니다.', '/image1/ListIcon/recipePic/82recipe10.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (527, 82, 11, '일정한 크기로 만들어 주세요.', '/image1/ListIcon/recipePic/82recipe11.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (528, 82, 12, '가열된 기름에 넣고 튀겨주세요.', '/image1/ListIcon/recipePic/82recipe12.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (529, 83, 1, '알새우는 물기를 제거하고 홍고추, 대파, 파프리카, 양파는 먹기 좋게 한입 크기로 썰어주세요.', '/image1/ListIcon/recipePic/83recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (530, 83, 2, '새우는 마리네이드 하여 잘 버무려 주세요.', '/image1/ListIcon/recipePic/83recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (531, 83, 3, '팬에 식용유 1/2컵을 넣고 끓어오르면 순서 2의 새우를 넣어 기름에 데치듯 부드럽게 익혀주세요.', '/image1/ListIcon/recipePic/83recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (532, 83, 4, '분량의 재료를 골고루 섞어서 소스를 만들어 주세요.', '/image1/ListIcon/recipePic/83recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (533, 83, 5, '팬에 식용유을 두르고 마른 월남 고추, 대파, 다진 마늘, 다진 생강을 볶아 향을 낸 후 다른 채소들도 함께 넣어 볶아 줍니다.', '/image1/ListIcon/recipePic/83recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (534, 83, 6, '잘 볶아진 채소 위에 준비된 순서 4의 소스를 넣어 끓인 다음 순서 3의 새우를 넣고 잘 버무려 완성해 주세요.', '/image1/ListIcon/recipePic/83recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (535, 84, 1, '달걀은 흰자와 노른자를 잘 섞고, 대파는 잘게 썰어 흰밥과 함께 준비해 주세요.', '/image1/ListIcon/recipePic/84recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (536, 84, 2, '달궈 놓은 팬에 식용유를 넣고 달걀물을 부어 스크램블을 만들어 주세요.', '/image1/ListIcon/recipePic/84recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (537, 84, 3, '달걀이 80%정도 익었을 때 밥을 넣어 고루 섞어가며 볶아주세요.', '/image1/ListIcon/recipePic/84recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (538, 84, 4, '잘게 썬 대파를 넣고 볶다가 치킨파우더, 소금, 후춧가루를 넣어 간을 해주세요.', '/image1/ListIcon/recipePic/84recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (539, 84, 5, '밥알이 서로 달라붙지 않게 노르스름해질 때까지 볶아준 후 참기름을 넣어 마무리 해주세요.', '/image1/ListIcon/recipePic/84recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (540, 85, 1, '양파와 호박은 깍둑썰기하고 돼지고기와 생강, 청양고추는 잘게 썰어 부추와 함께 준비해 주세요.', '/image1/ListIcon/recipePic/85recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (541, 85, 2, '오징어는 껍질을 벗겨 한입 크기로 썰고 알새우도 씻어서 준비해 주세요.', '/image1/ListIcon/recipePic/85recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (542, 85, 3, '달궈진 팬에 식용유를 두르고 돼지고기를 살짝 볶은 후 다진 생강과 청양고추를 함께 넣고 볶아서 향을 내주세요.', '/image1/ListIcon/recipePic/85recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (543, 85, 4, '순서 3의 고기가 익으면 양파와 호박을 넣어 충분히 익혀주고 볶은 춘장을 넣어 잘 섞어줍니다.', '/image1/ListIcon/recipePic/85recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (544, 85, 5, '순서 4에 물 1컵을 붓고 간장, 설탕, 치킨파우더를 넣어 간을 맞춰주세요. 기호에 따라 연두 청양초를 넣어 매콤한 맛을 더 살려줍니다. 그 다음 순서 2의 오징어와 알새우를 넣고 끓여주세요.', '/image1/ListIcon/recipePic/85recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (545, 85, 6, '생면을 잘 삶아서 씻은 다음 볶아놓은 순서 5의 짜장소스에 면을 넣고 잘 버무리고 물전분으로 농도를 맞추어 완성해 주세요. 기호에 맞게 부추를 함께 곁들이면 좋답니다.', '/image1/ListIcon/recipePic/85recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (546, 86, 1, '끓는 물에 분량의 육수 재료를 넣고 30분 정도 끓여서 육수를 내주세요.', '/image1/ListIcon/recipePic/86recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (547, 86, 2, '양파, 죽순, 표고버섯은 편 썰고 청경채는 4등분하여 준비해 주세요. 대파, 마늘, 생강은 다지고 쪽파는 흰 부분만 준비합니다.', '/image1/ListIcon/recipePic/86recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (548, 86, 3, '볼에 분량의 면 반죽 재료를 넣어 20분간 치댄 후 삼각형 모양으로 반죽해 주세요.', '/image1/ListIcon/recipePic/86recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (549, 86, 4, '냄비에 물을 끓여 순서 3의 반죽을 가위로 잘라서 넣고 익혀주세요.', '/image1/ListIcon/recipePic/86recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (550, 86, 5, '팬에 고추기름과 산초기름을 두르고 마늘, 생강, 쪽파, 대파, 팔각, 간장, 청주를 넣고 볶다가 두반장과 연두 청양초를 넣어 볶아주세요.', '/image1/ListIcon/recipePic/86recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (551, 86, 6, '순서 1의 육수에서 아롱사태를 꺼내 사방 2cm 크기로 썰어 그릇에 담고, 남은 육수와 순서 5를 넣고 치킨파우더, 백후춧가루, 소금으로 간을 해주세요. 그릇에 면을 담고 청경채와 고수를 얹으면 완성이랍니다.', '/image1/ListIcon/recipePic/86recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (552, 87, 1, '중국 당면은 미리 찬물에 30분간 담궈서 불려놔 주세요.', '/image1/ListIcon/recipePic/87recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (553, 87, 2, '모든 채소와 버섯은 채 썰고, 마늘, 대파는 다져서 준비해 주세요.', '/image1/ListIcon/recipePic/87recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (554, 87, 3, '고기는 채 썰어 마리네이드 한 후 기름에 볶아서 준비합니다.', '/image1/ListIcon/recipePic/87recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (555, 87, 4, '분량의 재료를 골고루 섞어서 소스를 만들어 주세요.', '/image1/ListIcon/recipePic/87recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (556, 87, 5, '팬에 기름을 두르고 대파, 마늘을 넣어 향을 내주세요. 모든 채소와 고기를 넣고 볶은 뒤 순서 4의 소스를 넣어 볶고 마지막에 당면을 넣고 마무리하면 완성이랍니다.', '/image1/ListIcon/recipePic/87recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (557, 88, 1, '가지는 채 썰고 홍고추, 청피망, 양파, 샐러리는 사방 0.5cm 크기로 썰어준 뒤, 다진 생강과 다진 마늘을 준비해 주세요.', '/image1/ListIcon/recipePic/88recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (558, 88, 2, '봉지에 가지와 감자 전분을 넣고 흔들어 튀김옷을 입혀주세요.', '/image1/ListIcon/recipePic/88recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (559, 88, 3, '가지가 잠길 만큼 식용유를 붓고 아지랑이가 보이면 가지를 넣어 바삭하게 튀깁니다.', '/image1/ListIcon/recipePic/88recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (560, 88, 4, '팬에 고추기름을 두르고 준비해둔 고기와 채소를 볶아주고 분량의 재료로 소스를 만들어 넣고 끓여주세요.', '/image1/ListIcon/recipePic/88recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (561, 88, 5, '순서 3의 가지를 넣고 볶다가 물전분으로 농도를 맞춰주세요.', '/image1/ListIcon/recipePic/88recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (562, 88, 6, '볶은 가지에 참기름을 뿌려주고 준비된 밥 위에 얹어 완성합니다.', '/image1/ListIcon/recipePic/88recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (563, 89, 1, '분량의 재료로 냉면 육수를 만들어 살얼음이 생기도록 살짝 얼려놔 주세요.', '/image1/ListIcon/recipePic/89recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (564, 89, 2, '해파리는 끓는 물에 살짝 삶아 부드러워질 때까지 찬물로 헹궈 소금기를 빼 주고, 오징어는 채 썰어 새우와 함께 끓는 물에 데쳐 차갑게 준비해 주세요.', '/image1/ListIcon/recipePic/89recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (565, 89, 3, '달걀은 지단으로 부치고 물에 불린 목이버섯, 당근, 오이는 채 썰어주세요.', '/image1/ListIcon/recipePic/89recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (566, 89, 4, '분량의 재료를 섞어서 땅콩 소스를 만들어 주세요.', '/image1/ListIcon/recipePic/89recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (567, 89, 5, '생면은 끓는 물에 삶아서 찬물로 헹군 다음 그릇에 담아 주세요.', '/image1/ListIcon/recipePic/89recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (568, 89, 6, '손질한 해산물과 채소를 면 위에 가지런히 올리고 차갑게 보관한 냉면 육수를 부은 뒤, 순서 4의 땅콩 소스를 곁들이면 완성이예요.', '/image1/ListIcon/recipePic/89recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (569, 90, 1, '볼에 달걀을 풀어주세요. 파는 송송 썰고 팽이버섯은 손으로 찢어 반으로 잘라주세요. 크래미는 손으로 찢어주세요.', '/image1/ListIcon/recipePic/90recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (570, 90, 2, '달걀에 손질한 재료를 넣어 섞어주세요.', '/image1/ListIcon/recipePic/90recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (571, 90, 3, '팬에 기름을 두르고 달걀물을 부어 뚜껑을 덮어 4분간 익혀주세요. 불을 끄고 5분간 뜸을 들여주세요.', '/image1/ListIcon/recipePic/90recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (572, 90, 4, '달걀을 그릇에 덜어놓은 후 팬에 전분을 제외한 양념을 넣고 끓여주세요.', '/image1/ListIcon/recipePic/90recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (573, 90, 5, '전분물을 조금씩 부어 1분간 저어가며 끓여주세요.', '/image1/ListIcon/recipePic/90recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (574, 90, 6, '밥 위에 달걀을 올리고 소스를 부어주세요.', '/image1/ListIcon/recipePic/90recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (575, 90, 7, '쪽파와 통깨를 뿌려 완성해주세요.', '/image1/ListIcon/recipePic/90recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (576, 91, 1, '해감한 바지락은 흐르는 물에 비벼 깨끗이 씻어주세요.', '/image1/ListIcon/recipePic/91recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (577, 91, 2, '오징어는 사선으로 칼집을 넣은 후 한입 크기로 썰어주세요.', '/image1/ListIcon/recipePic/91recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (578, 91, 3, '대파는 세로로 가른 후 5cm 길이로 썰어주세요. 양파, 당근, 호박은 채썰어 주세요. 배추와 청경채는 먹기 좋은 크기로 썰어주세요. 목이버섯은 30분 정도 물에 불려서 먹기 좋은 크기로 손질해 주세요.', '/image1/ListIcon/recipePic/91recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (579, 91, 4, '팬에 식용유를 두르고 다진 마늘, 다진 생강, 대파를 볶아 향을 내주세요. 돼지고기를 넣어 볶아주세요.', '/image1/ListIcon/recipePic/91recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (580, 91, 5, '청경채를 제외한 채소를 넣고 볶다가 간장과 고춧가루를 넣어 채소에 잘 흡수되도록 충분히 볶아주세요.', '/image1/ListIcon/recipePic/91recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (581, 91, 6, '팬에 준비된 물의 반을 넣고 소금, 치킨파우더, 후춧가루를 넣고 팔팔 끓여주세요. 채소가 육수에 우러나도록 끓인 후 나머지 물을 더 넣고 더 끓여주세요.', '/image1/ListIcon/recipePic/91recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (582, 91, 7, '손질한 해물을 넣어 끓이다가 마지막으로 청경채를 넣어 완성해 주세요.', '/image1/ListIcon/recipePic/91recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (583, 91, 8, '끓는 물에 중화면을 넣어 3~4분간 삶아주세요.', '/image1/ListIcon/recipePic/91recipe8.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (584, 91, 9, '채반에 밭쳐 찬물로 헹군 후 뜨거운 물에 한번 담갔다가 물기를 빼서 그릇에 담아주세요.', '/image1/ListIcon/recipePic/91recipe9.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (585, 91, 10, '완성된 육수를 부어 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/91recipe10.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (586, 92, 1, '양파는 깍둑썰어주세요.', '/image1/ListIcon/recipePic/92recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (587, 92, 2, '대파는 송송 썰어주세요.', '/image1/ListIcon/recipePic/92recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (588, 92, 3, '오이는 채를 썰어 준비해주세요.', '/image1/ListIcon/recipePic/92recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (589, 92, 4, '웍 팬에 식용유와 춘장을 넣고 3분간 볶아주세요. 볶은 춘장은 따로 덜어주세요.', '/image1/ListIcon/recipePic/92recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (590, 92, 5, '춘장을 볶은 기름에 다진 마늘과 대파를 넣고 볶아 향을 내주세요.', '/image1/ListIcon/recipePic/92recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (591, 92, 6, '양파를 넣고 2분간 볶아주세요.', '/image1/ListIcon/recipePic/92recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (592, 92, 7, '양파에 볶은 춘장과 후춧가루, 생강가루, 설탕을 넣고 표고 불린 물을 조금씩 추가하며 볶아주세요.', '/image1/ListIcon/recipePic/92recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (593, 92, 8, '전분 물을 넣고 저어 짜장 소스를 완성해주세요.', '/image1/ListIcon/recipePic/92recipe8.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (594, 92, 9, '끓는 물에 면을 넣고 8분간 삶은 후 흐르는 물에 헹궈 전분기를 빼주세요.', '/image1/ListIcon/recipePic/92recipe9.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (595, 92, 10, '그릇에 삶은 면과 짜장 소스를 담고 채를 썬 오이를 올려 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/92recipe10.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (596, 93, 1, '두부는 사방 2cm 크기로 썰고, 청피망, 홍피망, 마늘, 대파는 다져주세요. 브로콜리는 한입 크기로 썰어 준비합니다.', '/image1/ListIcon/recipePic/93recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (597, 93, 2, '봉지에 수분을 제거한 두부와 전분 1컵을 담아서 잘 섞어주세요.', '/image1/ListIcon/recipePic/93recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (598, 93, 3, '가열된 기름에 두부를 넣어 바삭하게 튀겨주세요.', '/image1/ListIcon/recipePic/93recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (599, 93, 4, '분량의 재료를 골고루 섞어서 소스를 만들어 주세요.', '/image1/ListIcon/recipePic/93recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (600, 93, 5, '팬에 기름을 두르고 대파, 마늘, 사천고추, 청피망, 홍피망, 브로콜리를 넣고 볶은 후 순서 3의 튀긴 두부와 순서 4의 소스를 넣고 맛있게 조리면 완성이예요.', '/image1/ListIcon/recipePic/93recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (601, 94, 1, '돼지고기 등심, 죽순, 표고버섯, 양파, 당근, 대파, 불린 목이버섯은 4cm 길이로 채 썰고, 마늘과 생강은 다져서 준비해 주세요.', '/image1/ListIcon/recipePic/94recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (602, 94, 2, '채 썬 돼지고기 등심은 마리네이드 해두었다가 팬에 볶아주세요.', '/image1/ListIcon/recipePic/94recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (603, 94, 3, '분량의 재료를 골고루 섞어서 소스를 만들어 주세요.', '/image1/ListIcon/recipePic/94recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (604, 94, 4, '팬에 고추기름을 두르고 다진 마늘과 생강을 넣고 볶다가 순서 3을 넣고 볶은 뒤, 채 썬 채소와 고기를 넣어 볶아주세요.', '/image1/ListIcon/recipePic/94recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (605, 94, 5, '순서 4에 참기름을 넣어 준비된 밥 위에 얹어 내면 완성이예요.', '/image1/ListIcon/recipePic/94recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (606, 95, 1, '돼지고기에 고기 양념 재료를 넣어 10분간 밑간해주세요.', '/image1/ListIcon/recipePic/95recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (607, 95, 2, '볼에 어향 소스 재료를 모두 넣어 섞어주세요.', '/image1/ListIcon/recipePic/95recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (608, 95, 3, '청고추와 당근, 홍고추, 양파, 표고버섯, 대파는 채를 썰고, 실파는 송송 썰어주세요.', '/image1/ListIcon/recipePic/95recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (609, 95, 4, '볼에 전분물 재료를 넣고 섞어주세요.', '/image1/ListIcon/recipePic/95recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (610, 95, 5, '달군 팬에 식용유와 고추기름을 두르고 대파를 넣어 볶아주세요.', '/image1/ListIcon/recipePic/95recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (611, 95, 6, '향이 올라오면 돼지고기를 넣어 볶아주세요.', '/image1/ListIcon/recipePic/95recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (612, 95, 7, '돼지고기가 반쯤 익으면 양파와 당근, 표고버섯, 어향 소스를 넣어 볶아주세요.', '/image1/ListIcon/recipePic/95recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (613, 95, 8, '치킨육수를 넣고 3분 정도 끓여주세요.', '/image1/ListIcon/recipePic/95recipe8.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (614, 95, 9, '마지막에 청고추와 홍고추, 전분물을 넣고 섞어준 후 참기름을 넣고 불을 꺼주세요.', '/image1/ListIcon/recipePic/95recipe9.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (615, 95, 10, '접시에 밥을 담고 야채와 볶은고기를 얹은 후 송송 썬 실파와 통깨를 뿌려주세요.', '/image1/ListIcon/recipePic/95recipe10.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (616, 96, 1, '오징어는 껍질을 벗기고 칼집을 넣어 먹기 좋게 썰어주세요.', '/image1/ListIcon/recipePic/96recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (617, 96, 2, '새우는 껍질을 벗기고 등부분에 칼집을 넣어 내장을 뺀 후 돼지고기와 함께 밑간 양념 재료에 버무려 밑간해주세요.', '/image1/ListIcon/recipePic/96recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (618, 96, 3, '팬에 기름을 넉넉히 넣어 달군 후 돼지고기와 새우를 볶아 체에 밭쳐 기름을 빼 준비해주세요.', '/image1/ListIcon/recipePic/96recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (619, 96, 4, '마늘과 대파, 표고버섯, 죽순은 채를 썰고, 팽이버섯은 밑동을 잘라내고 부추는 6센치 길이로 썰어주세요.', '/image1/ListIcon/recipePic/96recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (620, 96, 5, '죽순과 표고, 오징어는 끓는 물에 살짝 데친 후 체에 밭쳐 물기를 빼주세요.', '/image1/ListIcon/recipePic/96recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (621, 96, 6, '팬에 기름을 두르고 대파와 마늘 채를 넣어 볶아주세요.', '/image1/ListIcon/recipePic/96recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (622, 96, 7, '향이 올라오면 양념 소스 재료를 넣고 표고버섯과 죽순, 오징어를 넣어 볶아주세요.', '/image1/ListIcon/recipePic/96recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (623, 96, 8, '뜨거운 다시마육수를 넣고 육수가 끓으면 돼지고기와 새우를 넣고 녹말물을 넣어 걸쭉하게 농도를 맞쳐주세요.', '/image1/ListIcon/recipePic/96recipe8.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (624, 96, 9, '부추와 팽이버섯을 넣고 가볍게 섞고 마지막에 참기름을 넣은 후 불을 꺼주세요.', '/image1/ListIcon/recipePic/96recipe9.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (625, 96, 10, '그릇에 밥을 담고 밥 위에 유산슬을 얹어 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/96recipe10.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (626, 97, 1, '두부는 2센치 크기로 썰어주세요. 끓는 물에 약간의 소금을 넣고, 두부를 넣어 1분 정도 데쳐주세요.', '/image1/ListIcon/recipePic/97recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (627, 97, 2, '양파와 홍고추, 청고추는 잘게 자르고 대파, 실파는 송송 썰어주세요.', '/image1/ListIcon/recipePic/97recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (628, 97, 3, '달군 팬에 고추기름을 두르고 대파, 홍고추, 양파를 넣어 볶아주세요.', '/image1/ListIcon/recipePic/97recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (629, 97, 4, '다진 돼지고기와 양념재료를 넣고 볶다가 돼지고기가 익으면 청고추를 넣고, 물을 부어 2분 정도 더 끓여주세요.', '/image1/ListIcon/recipePic/97recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (630, 97, 5, '국물이 끓어오르면 두부를 넣고, 녹말물을 조금씩 넣으면서 농도를 걸쭉하게 맞춰 주세요. 참기름을 두르고 불을 꺼주세요.', '/image1/ListIcon/recipePic/97recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (631, 97, 6, '접시에 밥을 담고 마파두부를 올린 후, 송송 썬 실파를 뿌려주세요.', '/image1/ListIcon/recipePic/97recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (632, 98, 1, '넓적당면은 물에 1시간 정도 미리 불려서 끓는 물에 데친 후 찬물에 헹군 뒤 체에 받쳐 준비해 주세요.', '/image1/ListIcon/recipePic/98recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (633, 98, 2, '쪽파는 송송 썰고, 오이는 가운데 씨를 빼고 어슷 썰고, 생강과 청양고추는 채 썰어 준비해 주세요.', '/image1/ListIcon/recipePic/98recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (634, 98, 3, '팬에 파기름을 넣고, 팔각과 생강으로 향을 낸 후 쪽파, 청양고추, 치킨파우더, 소금, 간장, 설탕을 넣고 끓여주세요.', '/image1/ListIcon/recipePic/98recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (635, 98, 4, '순서 1의 당면을 오이와 섞어 그릇에 담은 후 순서 3의 소스를 부어 비벼주세요.', '/image1/ListIcon/recipePic/98recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (636, 98, 5, '마른 고추 후레이크와 참기름을 얹어 완성해주세요.', '/image1/ListIcon/recipePic/98recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (637, 99, 1, '채소는 채 썰어 준비하고 물에 불린 목이버섯은 먹기 좋게 썰어놔 주세요.', '/image1/ListIcon/recipePic/99recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (638, 99, 2, '모시조개는 소금물에 담가 해감을 하고 오징어는 껍질을 벗겨 한입 크기로 썰어 알새우와 함께 준비해 주세요.', '/image1/ListIcon/recipePic/99recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (639, 99, 3, '생면은 잘 삶아서 찬물에 헹군 뒤 다시 뜨거운 물에 데쳐서 그릇에 담아 주세요.', '/image1/ListIcon/recipePic/99recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (640, 99, 4, '달궈진 팬에 식용유를 두르고 다진 마늘, 다진 생강, 청양고추, 마른 고추를 넣고 볶아서 매콤한 향을 내줍니다.', '/image1/ListIcon/recipePic/99recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (641, 99, 5, '채 썬 채소를 넣고 볶아가며 충분히 익혀준 후 물과 치킨파우더, 소금, 후춧가루로 간을 맞추고 순서 2의 손질된 해물을 넣고 끓여주세요.', '/image1/ListIcon/recipePic/99recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (642, 99, 6, '육수가 보글보글 끓어오르면 참기름을 넣고 순서 3의 면 위에 부어 완성해 주세요.', '/image1/ListIcon/recipePic/99recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (643, 100, 1, '양파, 호박, 죽순, 대파는 채 썰고, 목이버섯은 뜨거운 물에 담가 불린 뒤 청경채와 함께 먹기 좋게 썰어놓습니다.', '/image1/ListIcon/recipePic/100recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (644, 100, 2, '오징어는 안쪽에 칼집을 넣어 채 썰고, 새우는 등 부분의 내장을 제거하고 손질하여 홍합과 함께 준비해 주세요.', '/image1/ListIcon/recipePic/100recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (645, 100, 3, '팬에 식용유를 두르고 다진 마늘, 다진 생강, 대파를 두반장과 함께 볶아 향을 내주세요.', '/image1/ListIcon/recipePic/100recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (646, 100, 4, '순서 3에 1의 채소를 넣어 볶으면서 고춧가루를 넣고 채소에 잘 흡수되도록 계속 볶아주세요.', '/image1/ListIcon/recipePic/100recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (647, 100, 5, '순서 4에 물을 붓고 2의 해물을 넣어 끓여주세요. 육수가 잘 끓어오르면 분량의 물, 치킨파우더, 굴소스, 설탕, 소금, 후춧가루 를 넣어 양념을 해주세요.', '/image1/ListIcon/recipePic/100recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (648, 100, 6, '순서 5에 잘 삶아서 찬물로 헹군 생면을 넣고 물전분으로 농도를 맞춘 후 참기름을 넣어 완성해 주세요. 기호에 맞게 부추를 함께 곁들이면 좋답니다.', '/image1/ListIcon/recipePic/100recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (787, 121, 1, '물에 소금 1큰술을 넣어 끓여주세요. 끓는 물에 파스타면을 넣고 10~12분 정도 삶아주세요. 면수는 1컵 따로 덜어주세요.', '/image1/ListIcon/recipePic/121recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (788, 121, 2, '슬라이스 치즈는 잘게 썰어주세요. 볼에 달걀을 풀어 치즈를 넣고 섞어주세요.', '/image1/ListIcon/recipePic/121recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (789, 121, 3, '대파는 송송 썰어주세요.', '/image1/ListIcon/recipePic/121recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (790, 121, 4, '팬에 올리브오일을 두르고 다진마늘과 대파를 넣어 약불에서 볶아주세요. 삶아둔 파스타면과 면수, 치킨스톡을 넣고 중불에서 2분 정도 볶아주세요. 불을 약불로 낮춘 후 면수가 면에 잘 흡수 될 수 있도록 팬을 흔들어 주세요.', '/image1/ListIcon/recipePic/121recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (791, 121, 5, '볶은 면에 달걀 치즈물을 붓고 중불에서 가볍게 익혀주세요. 소금 1작은술을 넣어 간을 해 주세요.', '/image1/ListIcon/recipePic/121recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (792, 121, 6, '달걀이 부드럽게 익으면 불을 끄고 후춧가루를 뿌려 완성해 주세요.', '/image1/ListIcon/recipePic/121recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (793, 121, 7, '달걀파스타를 접시에 담아 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/121recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (794, 122, 1, '냄비에 꽃소금을 한 줌 넣고 물을 끓인 후 스파게티 면을 5분간 삶아주세요. 양파와 마늘은 곱게 다지고 베이컨을 1cm 크기로 썬 뒤 팬에 올리브유를 둘러 베이컨을 바삭하게 볶은 후 키친타올에 빼두세요.', '/image1/ListIcon/recipePic/122recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (795, 122, 2, '베이컨 기름으로 양파와 마늘을 볶아주세요.', '/image1/ListIcon/recipePic/122recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (796, 122, 3, '달걀은 흰자와 노른자를 분리하고 노른자는 풀어서 준비해 주세요.', '/image1/ListIcon/recipePic/122recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (797, 122, 4, '양파와 마늘을 볶은 팬에 치킨 스톡과 면을 넣고 스톡의 양이 반으로 줄어들 때까지 계속 비벼주세요. 불을 끄고 달걀을 넣어 면과 잘 비빈 후 그라나 파다노 치즈, 파슬리, 후추, 볶아 둔 베이컨을 올려 마무리합니다.', '/image1/ListIcon/recipePic/122recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (798, 123, 1, '양파는 채를 썰고 마늘은 편으로 썰어주세요. 양송이버섯은 슬라이스하고 아스파라거스는 밑동을 잘라내고 먹기 좋은 크기로 잘라주세요.', '/image1/ListIcon/recipePic/123recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (799, 123, 2, '통삼겹살에 밑간 재료를 뿌려 밑간해주세요.', '/image1/ListIcon/recipePic/123recipe2.jpg');
