insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (801, 123, 4, '체에 밭쳐 물기를 빼주세요.', '/image1/ListIcon/recipePic/123recipe4.jpg');
