insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (804, 123, 7, '양송이버섯과 아스파라거스를 넣어 소금, 후춧가루로 간을 해 볶아주세요.', '/image1/ListIcon/recipePic/123recipe7.jpg');
