insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (812, 124, 5, '냄비에 물을 반 정도 넣고 스파게티를 넣어 7분 정도 삶아주세요.', '/image1/ListIcon/recipePic/124recipe5.jpg');
