insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (818, 125, 1, '달군 팬에 다진 베이컨, 슬라이스한 마늘을 넣고 중불에서 2분간 볶아주세요.', '/image1/ListIcon/recipePic/125recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (819, 125, 2, '적당한 크기로 자른 피망과 어슷 썬 대파, 슬라이스한 양파, 양송이와 고운 고춧가루를 넣고 중불에서 2분간 볶아주세요.', '/image1/ListIcon/recipePic/125recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (820, 125, 3, '순서 2에 휘핑크림, 우유를 넣고 중불에서 저으며 3분 끓이다가 나머지 소스 재료를 넣고 2분 더 끓여주세요. 부족한 간은 소금으로 맞춰주세요', '/image1/ListIcon/recipePic/125recipe3.jpg');
