insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (844, 129, 2, '케일 데친 물 1/2컵과 케일을 믹서로 갈아주세요.', '/image1/ListIcon/recipePic/129recipe2.jpg');
