insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (846, 129, 4, '양파는 얇게 채 썰어 주세요. 마늘은 편 썰어 주세요.', '/image1/ListIcon/recipePic/129recipe4.jpg');
