insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (848, 129, 6, '삶은 파스타면을 넣고 골고루 섞은 후 접시에 담고 파르메산치즈(또는  그라나 파다노 치즈)를 뿌리고 후춧가루를 뿌려 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/129recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (849, 130, 1, '마늘은 편으로 썰고 페페론치노와 이태리파슬리는 굵게 다져주세요. 새우는 머리를 떼어내고 꼬리만 남겨 껍질을 제거해 주세요.', '/image1/ListIcon/recipePic/130recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (850, 130, 2, '물 8컵에 소금 1큰술을 넣고 스파게티 면을 6분 정도 삶고 넓은 그릇에 펼친 후 올리브오일을 뿌려주세요.', '/image1/ListIcon/recipePic/130recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (851, 130, 3, '팬에 올리브오일을 두르고 마늘 편과 페페론치노를 넣어 약간의 소금과 후추로 밑간하여 볶아주세요. 손질한 새우를 넣어 마늘이 노릇해질 때까지 볶아주세요.', '/image1/ListIcon/recipePic/130recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (852, 130, 4, '삶은 스파게티 면을 넣고 약간의 소금과 후추로 간을 하고 면과 소스가 겉돌지 않게 면수, 치킨스톡을 넣어주면서 볶아주세요. 마지막에 약간의 올리브오일과 이태리파슬리를 뿌리고 불을 꺼주세요.', '/image1/ListIcon/recipePic/130recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (853, 130, 5, '그릇에 담아 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/130recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (854, 131, 1, '깨끗이 씻은 토마토는 4~6등분으로 자르고, 양파는 채썰고 당근은 깍뚝 썰어주세요. 파슬리는 잘게 다져주세요.', '/image1/ListIcon/recipePic/131recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (855, 131, 2, '달군 냄비에 버터를 녹인 후 다진마늘과 양파를 넣어 양파가 투명해질 때까지 3~5분 정도 볶아주세요.', '/image1/ListIcon/recipePic/131recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (856, 131, 3, '토마토와 당근을 넣어 3~4분 볶다가 뜨거운 물에 치킨스톡을 넣어 녹이고, 월계수잎과 바질가루를 함께 넣어 주세요. 중약불에서 뭉근하게 10~15분 끓인 후, 불을 끄고 월계수잎을 건져주세요.', '/image1/ListIcon/recipePic/131recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (857, 131, 4, '끓인 재료를 블렌더로 갈아준 후 5~10분 정도 더 끓여주세요.', '/image1/ListIcon/recipePic/131recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (858, 131, 5, '바게트와 크루통을 프라이팬에 바삭하게 구워주세요.', '/image1/ListIcon/recipePic/131recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (859, 131, 6, '그릇에 수프를 담아 다진 파슬리를 뿌리고, 취향에 따라 파르메산 치즈와 노릇하게 구운 빵을 함께 곁들여 즐겨주세요.', '/image1/ListIcon/recipePic/131recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (860, 132, 1, '양파는 얇게 썰고 사과도 껍질을 벗겨 얇게 썰어주세요. 냄비에 올리브유를 두르고 양파와 사과를 볶아주세요.', '/image1/ListIcon/recipePic/132recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (861, 132, 2, '사과와 양파를 볶던 냄비에 생크림과 우유를 넣어 약불에서 20분간 끓이다가, 핸드 믹서로 사과를 곱게 갈아준 후 타임을 넣어 약불로 10분간 더 끓여주세요.', '/image1/ListIcon/recipePic/132recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (862, 132, 3, '소금과 후추를 기호에 따라 넣고 허브를 건져낸 후 접시에 담고 차이브를 얇게 썰어 곁들이면 완성이예요.', '/image1/ListIcon/recipePic/132recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (863, 133, 1, '양파와 감자는 채 썰어주세요.', '/image1/ListIcon/recipePic/133recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (864, 133, 2, '브로콜리는 먹기 좋은 크기로 가지를 나누어주세요.', '/image1/ListIcon/recipePic/133recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (865, 133, 3, '달군 팬에 버터를 넣고 감자, 양파를 넣고 약간의 소금, 후춧가루를 뿌려 볶다가 물을 붓고, 중불에서 재료가 무를 때까지 10분 정도 끓여주세요.', '/image1/ListIcon/recipePic/133recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (866, 133, 4, '물을 붓고 끓어오르면 브로콜리를 넣어 중간불에서 재료가 무를 때까지 끓여주세요.', '/image1/ListIcon/recipePic/133recipe4.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (867, 133, 5, '한 김 식혀서 블렌더에 부드럽게 갈아주세요.', '/image1/ListIcon/recipePic/133recipe5.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (868, 133, 6, '냄비에 생크림을 넣고 끓으면 파마산치즈가루를 넣고 소금, 후추로 간을 해주세요.', '/image1/ListIcon/recipePic/133recipe6.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (869, 133, 7, '그릇에 담아 마무리해주세요.', '/image1/ListIcon/recipePic/133recipe7.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (870, 134, 1, '양파와 양배추는 깍뚝 썰고 마늘은 편 썰어 주세요.', '/image1/ListIcon/recipePic/134recipe1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (871, 134, 2, '달군 팬에 올리브 오일을 두르고 마늘과 양파를 넣어 볶다가 소시지를 넣어 노릇하게 볶아주세요.', '/image1/ListIcon/recipePic/134recipe2.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (872, 134, 3, '소시지가 노릇하게 구워지면 양배추와 파프리카 가루를 넣어 볶아주세요. 레드 와인과 월계수 잎을 넣어 졸여 주세요.', '/image1/ListIcon/recipePic/134recipe3.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (873, 134, 4, '레드 와인이 졸여지면 토마토소스와 물을 넣고 한소끔 끓인 후 콩을 넣어 뭉근히 끓여주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (874, 134, 5, '완성된 스튜에 슈레드 치즈와 파슬리 잎을 넣고 치즈가 녹으면 그릇에 담아 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (875, 135, 1, '볼에 닭다리와 샬롯, 타임, 월계수잎을 넣고 와인을 부어 냉장고에서 12시간 숙성해주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (876, 135, 2, '통 베이컨은 잘게 깍뚝 썰고 양송이는 반으로 잘라 준비해주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (877, 135, 3, '숙성한 재료를 건져내고 와인을 채에 걸러주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (878, 135, 4, '달군 냄비에 올리브 오일을 두르고 닭다리를 껍질부터 구워주세요. 구운 닭고기를 접시에 덜어주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (879, 135, 5, '닭고기를 구운 냄비에 베이컨을 넣고 볶은 후 접시에 덜어주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (880, 135, 6, '베이컨을 볶은 냄비에 버터를 넣고 양송이를 볶은 후 접시에 덜어주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (881, 135, 7, '냄비에 버터를 넣고 샬롯을 볶다가 토마토 페이스트 1큰술과 밀가루 1컵을 넣고 볶은 후 체에 거른 레드와인과 스톡을 넣고 저어가며 끓여주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (882, 135, 8, '구워둔 닭다리, 베이컨, 양송이를 넣고 타임과 월계수잎을 올린 후 포일로 덮어 주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (883, 135, 9, '오븐에서 230도로 50분간 조리해 주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (884, 135, 10, '완성된 치킨스튜를 접시에 담고 파슬리를 뿌려 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (885, 136, 1, '토마토는 꼭지를 제거하고, 열십자(十)로 칼집을 넣어주세요. 끓는 물에 토마토를 살짝 데친 후 얼음물에 담가 껍질을 제거하고, 4등분으로 잘라주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (886, 136, 2, '오이, 파프리카를 큼직하게 썰고 샐러리는 줄기를 제거한 후 썰어주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (887, 136, 3, '믹서에 토마토, 오이, 파프리카, 샐러리, 마늘을 넣고 부드럽게 갈아 준 후 밑간 재료를 넣어 잘 섞어 주세요. 냉장고에 1시간 이상 숙성시켜 주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (888, 136, 4, '차가워진 토마토 가스파초를 그릇에 담아 잘게 자른 토마토와 샐러리, 바질 잎(생략 가능)을 올리고 약간의 후추, 올리브유를 뿌려주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (889, 137, 1, '냄비에 물 6컵, 굵은소금 1/2큰술, 설탕 2큰술, 옥수수를 넣고 뚜껑을 닫은 후 강불에서 30분 정도 삶고, 10분간 약불에서 뜸을 들여 삶아주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (890, 137, 2, '삶은 옥수수는 칼로 잘라 옥수수 알갱이를 분리해주세요. 감자, 양파는 껍질을 제거하고 굵게 채를 썰어주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (891, 137, 3, '달군 팬에 버터를 녹인 후 양파, 감자, 옥수수를 넣어 3분 정도 볶아주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (892, 137, 4, '우유 3컵을 넣고 감자가 익을 때까지 10분 정도 더 끓여주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (893, 137, 5, '한김 식혀 믹서기에 곱게 갈아주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (894, 137, 6, '믹서에 곱게 간 3과 생크림을 냄비에 넣은 후 약불에서 3분 정도 저어가며 끓여주세요. 올리고당, 소금, 후추를 넣고 간을 조절해주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (895, 137, 7, '콘수프에 삶은옥수수를 약간 올리고, 파슬리가루로 장식해주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (896, 138, 1, '모시조개는 소금물에 담가 해감한 후 깨끗이 씻어 냄비에 담고, 국물재료를 넣어 조개껍질이 벌어질 때까지 끓여주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (897, 138, 2, '조갯살은 발라내어 굵게 다지고, 1의 조개육수는 따로 체에 걸러주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (898, 138, 3, '마늘은 편으로 썰고 베이컨과 감자, 샐러리, 양파는 사방 1센치 크기로 잘라주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (899, 138, 4, '냄비에 버터를 넣어 녹인 후 마늘, 베이컨, 감자, 샐러리, 양파를 2분 정도 볶아주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (900, 138, 5, '밀가루를 넣고 약불에서 재료와 밀가루가 고루 섞일 때까지 더 볶아주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (901, 138, 6, '조개육수 1컵과 우유를 넣고 감자가 익을 때까지 10분 정도 끓여주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (902, 138, 7, '수프에 조개살을 넣고, 약간의 소금과 후추로 간을 해 2분 정도 더 끓여주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (903, 138, 8, '알맞은 그릇에 담아 파슬리가루를 뿌리고, 빵과 함께 곁들여드셔도 좋아요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (904, 139, 1, '닭가슴살은 우유 1/2컵에 20분 정도 담갔다가, 물에 씻은 후 밑간재료에 10분간 재워주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (905, 139, 2, '양송이, 양파, 파프리카는 한입크기로 자르고, 마늘은 편으로 썰어주세요. 블랙올리브와 건고추는 링으로 썰어주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (906, 139, 3, '냄비에 약간의 올리브유를 두르고, 건고추와 닭가슴살을 노릇하게 색이 나게 구워주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (907, 139, 4, '마늘, 양파, 양송이, 파프리카를 넣고 3분 정도 볶다가 소스재료를 넣고 20분 정도 끓여주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (908, 139, 5, '방울토마토와 블랙올리브를 넣어 5분 정도 중불로 끓여주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (909, 139, 6, '마지막에 소금, 후추로 간을 맞춘 후 파슬리가루를 뿌려 맛있게 즐겨주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
insert into recipe_Step (recipe_Step_Id, recipe_Id, step_Num, step_Way, step_Img) values (910, 140, 1, '단호박, 감자는 껍질을 제거해 잘게 썰고 양파는 채를 썰어 준비해주세요.', '/image1/ListIcon/recipePic/1seaweedSoup1.jpg');
